import React from 'react';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';
import useTodos from './hooks/useTodos';
import './styles/App.css';

function App() {
  const { todos, loading, error, addTodo, updateTodo, deleteTodo } = useTodos();

  const handleToggleComplete = async (id, currentCompleted) => {
    // For PATCH, we only send the fields that changed.
    // The API spec for PATCH allows `completed` to be updated.
    await updateTodo(id, { completed: !currentCompleted });
  };

  const handleEditTodo = async (id, updatedFields) => {
    // For PATCH, we send only the fields that are provided in updatedFields.
    // The API spec for PATCH allows `title` and `description` to be updated.
    await updateTodo(id, updatedFields);
  };

  if (loading) {
    return <div className="app-container">Loading todos...</div>;
  }

  if (error) {
    return <div className="app-container error-message">Error: {error.message || 'Failed to fetch todos.'}</div>;
  }

  return (
    <div className="app-container">
      <h1>My To-Do List</h1>
      <TodoForm onSubmit={addTodo} />
      <TodoList
        todos={todos}
        onToggleComplete={handleToggleComplete}
        onEdit={handleEditTodo}
        onDelete={deleteTodo}
      />
    </div>
  );
}

export default App;

