import axios from 'axios';

// Use environment variable for the API base URL
// In a Create React App project, this would be REACT_APP_API_BASE_URL
// For local development, you can set it in a .env file or directly here.
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:3000/api/v1';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// --- API Call Functions ---

/**
 * Retrieves a list of all To-Do items.
 * @returns {Promise<Array<Todo>>} A list of To-Do items.
 */
export const getTodos = async () => {
  try {
    const response = await api.get('/todos');
    return response.data;
  } catch (error) {
    console.error('Error fetching todos:', error.response?.data || error.message);
    throw error.response?.data || new Error('Failed to fetch todos');
  }
};

/**
 * Creates a new To-Do item.
 * @param {object} todoData - The To-Do item object to be created (TodoCreate schema).
 * @param {string} todoData.title - The title of the To-Do item.
 * @param {string} [todoData.description] - Optional detailed description.
 * @returns {Promise<Todo>} The created To-Do item.
 */
export const createTodo = async (todoData) => {
  try {
    const response = await api.post('/todos', todoData);
    return response.data;
  } catch (error) {
    console.error('Error creating todo:', error.response?.data || error.message);
    throw error.response?.data || new Error('Failed to create todo');
  }
};

/**
 * Retrieves a single To-Do item by ID.
 * @param {string} id - The unique identifier of the To-Do item.
 * @returns {Promise<Todo>} The To-Do item found.
 */
export const getTodoById = async (id) => {
  try {
    const response = await api.get(`/todos/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching todo with ID ${id}:`, error.response?.data || error.message);
    throw error.response?.data || new Error(`Failed to fetch todo with ID ${id}`);
  }
};

/**
 * Partially updates an existing To-Do item by ID.
 * @param {string} id - The unique identifier of the To-Do item.
 * @param {object} updatedFields - Fields to be updated (TodoUpdate schema).
 * @param {string} [updatedFields.title] - New title for the To-Do item.
 * @param {string} [updatedFields.description] - New description for the To-Do item.
 * @param {boolean} [updatedFields.completed] - New completion status.
 * @returns {Promise<Todo>} The updated To-Do item.
 */
export const patchTodo = async (id, updatedFields) => {
  try {
    const response = await api.patch(`/todos/${id}`, updatedFields);
    return response.data;
  } catch (error) {
    console.error(`Error patching todo with ID ${id}:`, error.response?.data || error.message);
    throw error.response?.data || new Error(`Failed to update todo with ID ${id}`);
  }
};

/**
 * Updates an existing To-Do item by ID (full replacement).
 * Note: This operation requires all fields from TodoCreate plus 'completed'.
 * @param {string} id - The unique identifier of the To-Do item.
 * @param {object} todoData - The To-Do item object with updated fields (TodoCreate & { completed: boolean }).
 * @param {string} todoData.title - The title of the To-Do item.
 * @param {string} [todoData.description] - Optional detailed description.
 * @param {boolean} todoData.completed - Completion status of the To-Do item.
 * @returns {Promise<Todo>} The updated To-Do item.
 */
export const putTodo = async (id, todoData) => {
  try {
    const response = await api.put(`/todos/${id}`, todoData);
    return response.data;
  } catch (error) {
    console.error(`Error putting todo with ID ${id}:`, error.response?.data || error.message);
    throw error.response?.data || new Error(`Failed to replace todo with ID ${id}`);
  }
};

/**
 * Deletes a To-Do item by ID.
 * @param {string} id - The unique identifier of the To-Do item.
 * @returns {Promise<void>} Resolves if deletion is successful.
 */
export const deleteTodo = async (id) => {
  try {
    await api.delete(`/todos/${id}`);
  } catch (error) {
    console.error(`Error deleting todo with ID ${id}:`, error.response?.data || error.message);
    throw error.response?.data || new Error(`Failed to delete todo with ID ${id}`);
  }
};

