import { useState, useEffect, useCallback } from 'react';
import { getTodos, createTodo, patchTodo, deleteTodo } from '../api';

/**
 * @typedef {object} Todo
 * @property {string} id
 * @property {string} title
 * @property {string} [description]
 * @property {boolean} completed
 * @property {string} createdAt
 * @property {string} updatedAt
 */

/**
 * Custom hook for managing To-Do items state and API interactions.
 * @returns {{todos: Todo[], loading: boolean, error: object|null, addTodo: Function, updateTodo: Function, deleteTodo: Function}}
 */
function useTodos() {
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTodos = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getTodos();
      setTodos(data);
    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchTodos();
  }, [fetchTodos]);

  const addTodo = useCallback(async (newTodoData) => {
    setError(null);
    try {
      const createdTodo = await createTodo(newTodoData);
      setTodos((prevTodos) => [...prevTodos, createdTodo]);
    } catch (err) {
      setError(err);
      throw err; // Re-throw to allow component to handle form errors
    }
  }, []);

  const updateTodo = useCallback(async (id, updatedFields) => {
    setError(null);
    // Optimistic update
    const originalTodos = todos;
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, ...updatedFields } : todo
      )
    );

    try {
      const updatedTodo = await patchTodo(id, updatedFields);
      // Ensure the state is consistent with the server response
      setTodos((prevTodos) =>
        prevTodos.map((todo) => (todo.id === id ? updatedTodo : todo))
      );
    } catch (err) {
      setError(err);
      setTodos(originalTodos); // Rollback on error
      throw err;
    }
  }, [todos]);

  const deleteTodo = useCallback(async (id) => {
    setError(null);
    // Optimistic update
    const originalTodos = todos;
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));

    try {
      await deleteTodo(id);
    } catch (err) {
      setError(err);
      setTodos(originalTodos); // Rollback on error
      throw err;
    }
  }, [todos]);

  return { todos, loading, error, addTodo, updateTodo, deleteTodo };
}

export default useTodos;

