import React, { useState } from 'react';
import '../styles/TodoForm.css';

/**
 * @typedef {object} TodoCreateData
 * @property {string} title
 * @property {string} [description]
 */

/**
 * Form component for creating new To-Do items.
 * @param {object} props
 * @param {(data: TodoCreateData) => Promise<void>} props.onSubmit - Function to call when the form is submitted.
 */
function TodoForm({ onSubmit }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [formError, setFormError] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormError(null);

    if (!title.trim()) {
      setFormError({ message: 'Title cannot be empty.' });
      return;
    }

    setIsSubmitting(true);
    try {
      await onSubmit({ title, description: description || null });
      setTitle('');
      setDescription('');
    } catch (err) {
      setFormError(err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form className="todo-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Add a new todo..."
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        disabled={isSubmitting}
        maxLength={255}
        required
      />
      <textarea
        placeholder="Description (optional)"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        disabled={isSubmitting}
      />
      <button type="submit" disabled={isSubmitting}>
        {isSubmitting ? 'Adding...' : 'Add Todo'}
      </button>
      {formError && (
        <p className="form-error">Error: {formError.message || 'Something went wrong.'}</p>
      )}
    </form>
  );
}

export default TodoForm;

