import React, { useState } from 'react';
import '../styles/TodoItem.css';

/**
 * @typedef {object} Todo
 * @property {string} id
 * @property {string} title
 * @property {string} [description]
 * @property {boolean} completed
 * @property {string} createdAt
 * @property {string} updatedAt
 */

/**
 * Displays a single To-Do item and handles actions (toggle, edit, delete).
 * @param {object} props
 * @param {Todo} props.todo - The To-Do item object.
 * @param {(id: string, completed: boolean) => Promise<void>} props.onToggleComplete - Callback for toggling completion status.
 * @param {(id: string, updatedFields: {title?: string, description?: string, completed?: boolean}) => Promise<void>} props.onEdit - Callback for editing the todo.
 * @param {(id: string) => Promise<void>} props.onDelete - Callback for deleting the todo.
 */
function TodoItem({ todo, onToggleComplete, onEdit, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(todo.title);
  const [editedDescription, setEditedDescription] = useState(todo.description || '');
  const [itemError, setItemError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const handleToggle = async () => {
    if (isProcessing) return;
    setIsProcessing(true);
    setItemError(null);
    try {
      await onToggleComplete(todo.id, todo.completed);
    } catch (err) {
      setItemError(err);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleSaveEdit = async () => {
    if (isProcessing) return;
    setItemError(null);

    const trimmedTitle = editedTitle.trim();
    if (!trimmedTitle) {
      setItemError({ message: 'Title cannot be empty.' });
      return;
    }

    const updatedFields = {};
    if (trimmedTitle !== todo.title) {
      updatedFields.title = trimmedTitle;
    }
    // Only send description if it changed or if it was null and now has a value
    if (editedDescription !== (todo.description || '')) {
      updatedFields.description = editedDescription || null; // Ensure empty string becomes null for API
    }

    if (Object.keys(updatedFields).length === 0) {
      setIsEditing(false); // No changes, just exit edit mode
      return;
    }

    setIsProcessing(true);
    try {
      await onEdit(todo.id, updatedFields);
      setIsEditing(false);
    } catch (err) {
      setItemError(err);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleDelete = async () => {
    if (isProcessing) return;
    if (!window.confirm(`Are you sure you want to delete "${todo.title}"?`)) {
      return;
    }
    setIsProcessing(true);
    setItemError(null);
    try {
      await onDelete(todo.id);
    } catch (err) {
      setItemError(err);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditedTitle(todo.title);
    setEditedDescription(todo.description || '');
    setItemError(null);
  };

  return (
    <li className={`todo-item ${todo.completed ? 'completed' : ''}`}>
      <div className="todo-content">
        <input
          type="checkbox"
          checked={todo.completed}
          onChange={handleToggle}
          disabled={isProcessing}
        />
        {isEditing ? (
          <div className="edit-form">
            <input
              type="text"
              value={editedTitle}
              onChange={(e) => setEditedTitle(e.target.value)}
              disabled={isProcessing}
              maxLength={255}
              required
            />
            <textarea
              value={editedDescription}
              onChange={(e) => setEditedDescription(e.target.value)}
              disabled={isProcessing}
            />
          </div>
        ) : (
          <div className="todo-text">
            <span className="todo-title">{todo.title}</span>
            {todo.description && <p className="todo-description">{todo.description}</p>}
            <div className="todo-timestamps">
              <small>Created: {new Date(todo.createdAt).toLocaleString()}</small>
              <small>Updated: {new Date(todo.updatedAt).toLocaleString()}</small>
            </div>
          </div>
        )}
      </div>

      <div className="todo-actions">
        {isEditing ? (
          <>
            <button onClick={handleSaveEdit} disabled={isProcessing} className="save-button">
              {isProcessing ? 'Saving...' : 'Save'}
            </button>
            <button onClick={handleCancelEdit} disabled={isProcessing} className="cancel-button">
              Cancel
            </button>
          </>
        ) : (
          <>
            <button onClick={() => setIsEditing(true)} disabled={isProcessing} className="edit-button">
              Edit
            </button>
            <button onClick={handleDelete} disabled={isProcessing} className="delete-button">
              {isProcessing ? 'Deleting...' : 'Delete'}
            </button>
          </>
        )}
      </div>
      {itemError && (
        <p className="item-error">Error: {itemError.message || 'Something went wrong.'}</p>
      )}
    </li>
  );
}

export default TodoItem;

