import React from 'react';
import TodoItem from './TodoItem';
import '../styles/TodoList.css';

/**
 * @typedef {object} Todo
 * @property {string} id
 * @property {string} title
 * @property {string} [description]
 * @property {boolean} completed
 * @property {string} createdAt
 * @property {string} updatedAt
 */

/**
 * Displays a list of To-Do items.
 * @param {object} props
 * @param {Todo[]} props.todos - An array of To-Do item objects.
 * @param {(id: string, completed: boolean) => Promise<void>} props.onToggleComplete - Callback for toggling completion status of a todo.
 * @param {(id: string, updatedFields: {title?: string, description?: string, completed?: boolean}) => Promise<void>} props.onEdit - Callback for editing a todo.
 * @param {(id: string) => Promise<void>} props.onDelete - Callback for deleting a todo.
 */
function TodoList({ todos, onToggleComplete, onEdit, onDelete }) {
  if (todos.length === 0) {
    return <p className="no-todos-message">No todos yet! Add one above.</p>;
  }

  return (
    <ul className="todo-list">
      {todos.map((todo) => (
        <TodoItem
          key={todo.id}
          todo={todo}
          onToggleComplete={onToggleComplete}
          onEdit={onEdit}
          onDelete={onDelete}
        />
      ))}
    </ul>
  );
}

export default TodoList;

