  min-height: 40px;
  resize: vertical;
}

.item-error {
  color: #e74c3c;
  font-size: 0.85em;
  margin-top: 10px;
  width: 100%;
  text-align: center;
}

/* Responsive adjustments */
@media (max-width: 600px) {
  .todo-item {
    flex-direction: column;
    align-items: flex-start;
  }

  .todo-content {
    width: 100%;
    margin-bottom: 15px;
    margin-right: 0;
  }

  .todo-actions {
    width: 100%;
    justify-content: flex-end;
    margin-top: 0;
  }

  .todo-actions button {
    flex-grow: 1;
  }
}
